document.addEventListener("DOMContentLoaded", () => { document.querySelectorAll('a[href^="#"]').forEach(e => { e.addEventListener("click", function (t) { t.preventDefault(); const n = document.querySelector(this.getAttribute("href")); n && n.scrollIntoView({ behavior: "smooth", block: "start" }) }) }); const e = document.getElementById("deleteForm"); e && e.addEventListener("submit", function (t) { t.preventDefault(); const n = document.getElementById("name").value, o = document.getElementById("email").value, c = document.getElementById("reason").value, l = `Name: ${n}\nEmail: ${o}\nReason: ${c}\n\nPlease delete my account and all associated data.`; window.location.href = `mailto:fahmidevstuff@gmail.com?subject=${encodeURIComponent("Account Deletion Request")}&body=${encodeURIComponent(l)}` }) });
